#!/usr/bin/env python3
"""
Test script to verify WebSocket connection fix
Tests connection stability during story generation simulation
"""

import asyncio
import websockets
import json
import time
import logging
from datetime import datetime

# Configure logging
logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)

async def test_websocket_connection():
    """Test WebSocket connection stability"""
    
    uri = "ws://localhost:8000/ws/video"
    
    try:
        logger.info("üîå Connecting to WebSocket...")
        async with websockets.connect(uri) as websocket:
            logger.info("‚úÖ Connected to WebSocket")
            
            # Test 1: Basic connection
            logger.info("üì° Testing basic connection...")
            
            # Send ping
            ping_message = {
                "type": "ping",
                "timestamp": datetime.utcnow().isoformat()
            }
            await websocket.send(json.dumps(ping_message))
            logger.info("üì§ Sent ping message")
            
            # Wait for pong
            response = await asyncio.wait_for(websocket.recv(), timeout=5.0)
            response_data = json.loads(response)
            
            if response_data.get("type") == "pong":
                logger.info("‚úÖ Received pong response - basic connection working")
            else:
                logger.warning(f"‚ö†Ô∏è Unexpected response: {response_data}")
            
            # Test 2: Connection stability during long operation
            logger.info("‚è±Ô∏è Testing connection stability during long operation...")
            
            # Simulate story generation delay
            for i in range(10):
                await asyncio.sleep(2)  # 2 second intervals
                
                # Send keepalive ping
                ping_message = {
                    "type": "ping",
                    "timestamp": datetime.utcnow().isoformat(),
                    "test_iteration": i + 1
                }
                await websocket.send(json.dumps(ping_message))
                logger.info(f"üì§ Sent keepalive ping {i + 1}/10")
                
                # Wait for response
                try:
                    response = await asyncio.wait_for(websocket.recv(), timeout=3.0)
                    response_data = json.loads(response)
                    
                    if response_data.get("type") == "pong":
                        logger.info(f"‚úÖ Received pong {i + 1}/10 - connection stable")
                    else:
                        logger.info(f"üì® Received: {response_data.get('type', 'unknown')}")
                        
                except asyncio.TimeoutError:
                    logger.warning(f"‚ö†Ô∏è Timeout waiting for pong {i + 1}/10")
                    break
            
            # Test 3: Frame sending simulation
            logger.info("üé• Testing frame sending simulation...")
            
            # Send mock frame data
            mock_frame = {
                "type": "raw_frame",
                "frame_data": "data:image/jpeg;base64,",
                "metadata": {
                    "frame_number": 1,
                    "timestamp": datetime.utcnow().isoformat(),
                    "width": 640,
                    "height": 480
                }
            }
            
            await websocket.send(json.dumps(mock_frame))
            logger.info("üì§ Sent mock frame data")
            
            # Wait for processed frame response
            try:
                response = await asyncio.wait_for(websocket.recv(), timeout=10.0)
                response_data = json.loads(response)
                
                if response_data.get("type") == "processed_frame":
                    logger.info("‚úÖ Received processed frame response - frame processing working")
                    logger.info(f"   Frame metadata: {response_data.get('metadata', {})}")
                else:
                    logger.info(f"üì® Received: {response_data.get('type', 'unknown')}")
                    
            except asyncio.TimeoutError:
                logger.warning("‚ö†Ô∏è Timeout waiting for processed frame response")
            
            logger.info("‚úÖ WebSocket connection test completed successfully")
            
    except websockets.exceptions.ConnectionClosed as e:
        logger.error(f"‚ùå WebSocket connection closed: {e}")
    except Exception as e:
        logger.error(f"‚ùå WebSocket test failed: {e}")

if __name__ == "__main__":
    logger.info("üß™ Starting WebSocket connection fix test...")
    asyncio.run(test_websocket_connection())
    logger.info("üèÅ WebSocket test completed")